*/

// @mui material components
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import React, { useEffect } from "react";
import Paper from '@mui/material/Paper';
// Material Dashboard 2 React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import Footer from "examples/Footer";
import { useState } from 'react';
import Stack from '@mui/material/Stack';
import { Tabs, Tab, Divider } from '@mui/material';
// Data
import MDButton from "components/MDButton";
//modal
import TextField from '@mui/material/TextField';
import { useNavigate } from 'react-router-dom';
import { styled } from '@mui/material/styles';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import InputLabel from '@mui/material/InputLabel';
function Test() {
  const [option, setOption] = React.useState('');
  const [test, setTest] = React.useState('');
  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));
  const handleChange = (event) => {
    setTest(event.target.value);
  };
  const handleOptionChange = (event) => {
    setTestCreationData({ ...testCreationData, testType: event.target.value })
    setOption(event.target.value);
  }

  const [testtype, setTesttype] = React.useState([]);
  const [open, setOpen] = useState(false);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const navigate = useNavigate();

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };
  const handleChangeSelect = (event) => {
    const {
      target: { value },
    } = event;
    setTesttype(
      // On autofill we get a stringified value.
      typeof value === 'string' ? value.split(',') : value,
    );
  };
  const handleButtonClick = () => {
    // Navigate to the next page when the button is clicked
    navigate('/DrData');
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const [QuestionBank, setQuestionBank] = React.useState('');
  const [codeQuestionBank, codesetQuestionBank] = React.useState('');
  const handleQuestionBankChange = (event) => {
    setTestCreationData({ ...testCreationData, bankName: event.target.value })
    setQuestionBank(event.target.value);

  }
  const handleCodeQuestionBankChange = (event) => {
    setcodeTestCreationData({ ...codeTestCreationData, bankName: event.target.value })
    setQuestionBank(event.target.value);
  }
  const [numInputs, setNumInputs] = React.useState(0);
  const handleNumInputsChange = (event) => {
    const value = parseInt(event.target.value);
    setNumInputs(value);
  };
  const [dropDownData, setDropDownData] = useState([]);
  useEffect(() => {
    const dropDown = async () => {
      try {
        const response = await fetch("http://localhost:5001/drop");
        const result = await response.json();
        console.log(result);
        setDropDownData(result);
      } catch (error) {
        console.error("Error fetching test data:", error);
      }
    };
    dropDown();
  }, [])
  const [codedropDownData, setCodeDropDownData] = useState([]);
  useEffect(() => {
    const codedropDown = async () => {
      try {
        const response = await fetch("http://localhost:5001/codeDrop");
        const result = await response.json();
        setCodeDropDownData(result);
      } catch (error) {
        console.error("Error fetching test data:", error);
      }
    };
    codedropDown();
  })

  const [testCreationData, setTestCreationData] = useState({
    testName: "",
    testType: "",
    date: "",
    duration: "",
    startTime: "",
    BankID: "",
    questionCount: "",
  })
  const [codeTestCreationData, setcodeTestCreationData] = useState({
    codeTestName: "",
    codeTestType: "",
    codeDate: "",
    codeDuration: "",
    codeStartTime: "",
    codeBankID: "",
    codeQuestionCount: "",
  })
  const handleTestCreate = async () => {
    try {
      const response = await fetch(`http://localhost:5001/staffTestCreation`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(testCreationData),
      });
      const result = await response.json();
      //toast("question bank created")
      alert("success");
    }
    catch (error) {
      console.log(error);
    }
  }
  const handleCodeTestCreate = async () => {
    console.log(codeTestCreationData)
    try {
      const response = await fetch(`http://localhost:5001/staffCodeTestCreation`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json"
