/**
=========================================================
* Material Dashboard 2 React - v2.2.0
=========================================================

* Product Page: https://www.creative-tim.com/product/material-dashboard-react
* Copyright 2023 Creative Tim (https://www.creative-tim.com)

Coded by www.creative-tim.com

 =========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
*/

// @mui material components
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import React, { useEffect } from "react";
import Paper from '@mui/material/Paper';
// Material Dashboard 2 React example components
import DashboardLayout from "examples/LayoutContainers/DashboardLayout";
import Footer from "examples/Footer";
import { useState } from 'react';
import Stack from '@mui/material/Stack';
import { Tabs, Tab, Divider } from '@mui/material';
// Data
import MDButton from "components/MDButton";
//modal
import TextField from '@mui/material/TextField';
import { useNavigate } from 'react-router-dom';
import { styled } from '@mui/material/styles';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import InputLabel from '@mui/material/InputLabel';
function Test() {
  const [option, setOption] = React.useState('');
  const [test, setTest] = React.useState('');
  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));
  const handleChange = (event) => {
    setTest(event.target.value);
  };
  const handleOptionChange = (event) => {
    setOption(event.target.value);
  }

  const [testtype, setTesttype] = React.useState([]);
  const [open, setOpen] = useState(false);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const navigate = useNavigate();

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };
  const handleChangeSelect = (event) => {
    const {
      target: { value },
    } = event;
    setTesttype(
      // On autofill we get a stringified value.
      typeof value === 'string' ? value.split(',') : value,
    );
  };
  const handleButtonClick = () => {
    // Navigate to the next page when the button is clicked
    navigate('/DrData');
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const [QuestionBank, setQuestionBank] = React.useState('');
  const handleQuestionBankChange = (event) => {
    setQuestionBank(event.target.value);
  }
  const [numInputs, setNumInputs] = React.useState(0);
  const handleNumInputsChange = (event) => {
    const value = parseInt(event.target.value);
    setNumInputs(value);
  };
  const [dropDownData,setDropDownData]=useState([]);
  useEffect(()=>{
    const dropDown = async () => {
      try {
        const response = await fetch("http://localhost:5001/drop");
        const result = await response.json();
        console.log(result);
        setDropDownData(result);
      } catch (error) {
        console.error("Error fetching test data:", error);
      }
    };
    dropDown();
  },[])


  return (
    <DashboardLayout>
      <MDBox pt={6} pb={3}>
        <Grid container spacing={6}>
          <Grid item xs={12}>
            <Card>
              <MDBox
                mx={2}
                mt={-3}
                py={3}
                px={2}
                variant="gradient"
                bgColor="info"
                borderRadius="lg"
                coloredShadow="info"
                display="flex"
                justifyContent="space-between" // To space elements evenly
                alignItems="center" // Align items vertically
              >
                <MDTypography variant="h6" color="white">
                  CREATE TEST
                </MDTypography>
              </MDBox>
              <MDBox pt={3}>
                <MDBox sx={{ height: 'autp' }}>
                  <Stack spacing={2} alignItems="center" sx={{ marginTop: '20px' }}>
                    <TextField id="outlined-basic" label="Test Name" variant="outlined" sx={{ width: '40%' }} />
                    <FormControl variant="outlined" sx={{ width: '40%' }}>
                      <InputLabel>Select test type</InputLabel>
                      <Select
                        label="Test Name"
                        value={option}
                        onChange={handleOptionChange}
                        variant="outlined"
                        sx={{ height: '50px' }}
                      >
                        <MenuItem value="">None</MenuItem>
                        <MenuItem value={10}>MCQ</MenuItem>
                        <MenuItem value={20}>Coding</MenuItem>
                        <MenuItem value={30}>Coding and MQ</MenuItem>
                      </Select>
                    </FormControl>
                    <div>
                      {option === 10 && (
                        <MDBox display="flex" flexDirection="column" alignItems="center">
                          {/* Layout for Option 1 */}
                          <TextField id="outlined-basic" type="date" variant="outlined" sx={{ width: '640px', marginBottom: '10px' }} />
                          <TextField id="outlined-basic" type="time" variant="outlined" sx={{ width: '640px', marginBottom: '10px' }} />
                          <TextField id="outlined-basic" type="number" label="Duration in (hrs)" variant="outlined" sx={{ width: '640px', marginBottom: '10px' }} />
                          <FormControl variant="outlined" sx={{ width: '640px', marginBottom: '10px' }}>
                            <InputLabel>Select Question Bank</InputLabel>
                            <Select
                              label="Select Question Bank"
                              value={QuestionBank}
                              onChange={handleQuestionBankChange}
                              variant="outlined"
                              sx={{ height: '50px' }}
                            >
                              <MenuItem value="">None</MenuItem>
                            {dropDownData && dropDownData.map((bank) => (
                              <MenuItem key={bank.id} value={bank.id}>{bank.name}</MenuItem>
                            ))}
                            </Select>
                          </FormControl>
                          <MDButton color="info">Create Test</MDButton>
                        </MDBox>
                      )}
                      {option === 20 && (
                        <MDBox display="flex" flexDirection="column" alignItems="center">
                          {/* Layout for Option 2 */}
                          <TextField id="outlined-basic" type="date" variant="outlined" sx={{ width: '640px', marginBottom: '10px' }} />
                          <TextField id="outlined-basic" type="time" variant="outlined" sx={{ width: '640px', marginBottom: '10px' }} />
                          <TextField id="outlined-basic" type="number" label="Duration in (hrs)" variant="outlined" sx={{ width: '640px', marginBottom: '10px' }} />
                          <TextField
                            id="num-inputs"
                            label="Number of Questions"
                            variant="outlined"
                            type="number"
                            value={numInputs}
                            onChange={handleNumInputsChange}
                            sx={{ width: '640px', marginBottom: '10px' }}
                          />

                          <MDButton color="info" sx={{ marginBottom: '10px' }}> create test</MDButton>
                        </MDBox>
                      )}

                      {option === 30 && (
                        <div>
                          {/* Layout for Option 3 */}
                          Option 3 Layout
                        </div>
                      )}
                    </div>
                  </Stack>
                </MDBox>
              </MDBox>
            </Card>

          </Grid>
        </Grid>
      </MDBox>
      <Footer />
    </DashboardLayout>
  );
}

export default Test;
