import { useSession } from " SessionContext";
import { useEffect } from "react";
function Student() {
  const { name, pass } = useSession();
  const [modal, setModal] = useState(false)
  const handleClose = () => setModal(false)
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const navigate = useNavigate();
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [isAsc, setIsAsc] = useState(true);

const [editFormData, setEditFormData] = useState({
  id: null,
  Name: "",
  email: "",
  phone: "",
  Position: "",
  department: ""
});

const handleEditOpen = (data) => {
  setEditFormData(data);
  setEditModalOpen(true);
};

const handleEditClose = () => {
  setEditModalOpen(false);
};

const positionOptions = ["HOD", "Assistant HOD", "Assistant Professor", "Lab Assistant"];
const departmentOptions = ["CSE", "ECE","Placement","EEE","Civil","Mechanical","Admission","HR","IT","Freshers"];



const [formData, setFormData] = useState({
  id: null,
  name: "", // Employee name
  email: "", // Employee email
  password: "", // Employee password
  phone: "", // Employee phone
  position: "", // Employee position
  department: "" // Employee department
});
  const [studenTableData, setStudentTableData] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5001/employeeData`);
        const jsonData = await response.json();
        setStudentTableData(jsonData);
      } catch (error) {
        console.error('Error fetching employee data:', error);
      }
    };
    fetchData();
  }, []);

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5001/deleteEmployee/${id}`, {
        method: "DELETE",
      });
      const result = await response.json();
      alert("Employee deleted successfully");
      // Fetch data again or update local state if needed
    } catch (error) {
      console.log(error);
    }
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch(`http://localhost:5001/employeeInsert`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData),
      });
      const result = await response.json();
      alert("Employee added successfully");
      setModal(false);
      // Fetch data again or update local state if needed
    } catch (error) {
      console.log(error);
    }
  };

  const handleSort = () => {
    const sortedData = [...studenTableData];
    sortedData.sort((a, b) => {
      if (a.department < b.department) return -1;
      if (a.department > b.department) return 1;
      return 0;
    });

    if (isAsc) {
      setStudentTableData(sortedData);
    } else {
      setStudentTableData(sortedData.reverse());
    }

    setIsAsc(!isAsc);
  };


  const handleOpen = () => {
    setModal(true);
  };

  // const handleButtonClick = () => {
  //   // Navigate to the next page when the button is clicked
  //   navigate('/DrData');
  // };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const handleEditSubmit = async () => {
    try {
      const response = await fetch(`http://localhost:5001/updateEmployee`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          editFormData
        }),
      });
      const result = await response.json();
      // Update the local state or fetch data again if needed
      // Close the edit modal
      setEditModalOpen(false);
    } catch (error) {
      console.log(error);
    }
  };



  const columns = [
    { Header: "SI.No", accessor: "SI_NO", width: "15%", align: "left" },
    { Header: "Id", accessor: "Id", width: "15%", align: "left" },
    { Header: "Name", accessor: "Name", width: "15%", align: "left" },
    { Header: "Email", accessor: "email", align: "left" },
    { Header: "phone", accessor: "phone", align: "center" },
    { Header: "Position", accessor: "Position", align: "center" },
    { 
      Header: () => (
        <div style={{ display: 'flex', alignItems: 'center', cursor: 'pointer' }} onClick={handleSort}>
          <span style={{ fontSize: '1.2em' }}>Department</span>
          {isAsc ? <span style={{ fontSize: '1.8em', marginLeft: '4px' }}>&uarr;</span> : <span style={{ fontSize: '1.8em', marginLeft: '4px' }}>&darr;</span>}
        </div>
      ),
      accessor: "department",
      align: "left"
    },
    { Header: "action", accessor: "Action", align: "center" },
  ]


  const rows = studenTableData.map((data, index) => ({
    SI_NO: index + 1,
    Id : data.id,
    Name: data.name,
    email: data.email,
    phone: data.phone,
    Position: data.position,
    department: data.department,
    Action: (
      <Box display="flex" justifyContent="space-between">
        <MDButton color="info" onClick={() => handleEditOpen(data)}>
          Edit
        </MDButton>
        <MDButton color="error" onClick={() => handleDelete(data.id)}>
          Delete
        </MDButton>
      </Box>
    ),
  }));


  return (
    <DashboardLayout>

      <MDBox pt={6} pb={3}>
        <Grid container spacing={6}>
          <Grid item xs={12}>
            <Card>
              <MDBox
                mx={2}
                mt={-3}
                py={3}
                px={2}
                variant="gradient"
                bgColor="info"
                borderRadius="lg"
                coloredShadow="info"
                display="flex"
                justifyContent="space-between" // To space elements evenly
                alignItems="center" // Align items vertically
              >
                <Dialog maxWidth="md" open={modal} justifyContent="center" onClose={handleClose} PaperProps={{
    style: {
      width: '600px', // Set desired width
      height: '500px' // Set desired height
    },
  }} >
                <DialogTitle>Employee Details</DialogTitle>
<DialogContent>
  <DialogContentText>
  <Box mb={2} mt={2}>
    <TextField
      id="employee-id"
      label="Id"
      value={formData.id}
      onChange={(e) =>
        setFormData({ ...formData, id: e.target.value })
      }
      variant="outlined"
      fullWidth

    />
    </Box>
  <Box mb={2} mt={2}>
    <TextField
      id="employee-name"
      label="Name"
      value={formData.name}
      onChange={(e) =>
        setFormData({ ...formData, name: e.target.value })
      }
      variant="outlined"
      fullWidth

    />
    </Box>
    <Box mb={2}>
    <TextField
      id="employee-email"
      label="Email"
      value={formData.email}
      onChange={(e) =>
        setFormData({ ...formData, email: e.target.value })
      }
      variant="outlined"
      fullWidth
    />
    </Box>
    <Box mb={2}>
    <TextField
      id="employee-password"
      label="Password"
      type="password"
      value={formData.password}
      onChange={(e) =>
        setFormData({ ...formData, password: e.target.value })
      }
      variant="outlined"
      fullWidth
    />
    </Box>
    <Box mb={2}>
    <TextField
      id="employee-phone"
      label="Phone"
      value={formData.phone}
      onChange={(e) =>
        setFormData({ ...formData, phone: e.target.value })
      }
      variant="outlined"
      fullWidth
    />
    </Box>
    <Box mb={2}>
  <Autocomplete
    id="employee-position"
    options={positionOptions}
    renderInput={(params) => <TextField {...params} label="Position" variant="outlined" />}
    value={formData.position}
    onChange={(event, newValue) => setFormData({ ...formData, position: newValue })}
    fullWidth
  />
</Box>
<Box mb={2}>
  <Autocomplete
    id="employee-department"
    options={departmentOptions}
    renderInput={(params) => <TextField {...params} label="Department" variant="outlined" />}
    value={formData.department}
    onChange={(event, newValue) => setFormData({ ...formData, department: newValue })}
    fullWidth
  />
</Box>

  </DialogContentText>
</DialogContent>

                  <DialogActions>
                    <MDButton onClick={handleClose} color="primary">
                      Cancel
                    </MDButton>
                    <MDButton onClick={handleSubmit} color="primary" autoFocus>
                      Save
                    </MDButton>
                  </DialogActions>
                </Dialog>
                <Dialog maxWidth="md" open={editModalOpen} onClose={handleEditClose}  PaperProps={{
    style: {
      width: '600px', // Set desired width
      height: '500px' // Set desired height
    },
  }}>
  <DialogTitle>Edit Employee Details</DialogTitle>
  <DialogContent>
    <DialogContentText>
    <Box mb={2} mt={2}>
      <TextField
        id="edit-name"
        label="Name"
        value={editFormData.name}
        onChange={(e) =>
          setEditFormData({ ...editFormData, Name: e.target.value })
        }
        variant="outlined"
        fullWidth
      />
      </Box>
      <Box mb={2}>
      <TextField
        id="edit-email"
        label="Email"
        value={editFormData.email}
        onChange={(e) =>
          setEditFormData({ ...editFormData, email: e.target.value })
        }
        variant="outlined"
        fullWidth
      />
      </Box>
      <Box mb={2}>
      <TextField
        id="edit-phone"
        label="Phone"
        value={editFormData.phone}
        onChange={(e) =>
          setEditFormData({ ...editFormData, phone: e.target.value })
        }
        variant="outlined"
        fullWidth
      />
      </Box>
      <Box mb={2}>
  <Autocomplete
    id="edit-position"
    options={positionOptions}
    value={editFormData.Position}
    onChange={(event, newValue) =>
      setEditFormData({ ...editFormData, Position: newValue })
    }
    renderInput={(params) => (
      <TextField {...params} label="Position" variant="outlined" fullWidth />
    )}
  />
</Box>
<Box mb={2}>
  <Autocomplete
    id="edit-department"
    options={departmentOptions}
    value={editFormData.department}
    onChange={(event, newValue) =>
      setEditFormData({ ...editFormData, department: newValue })
    }
    renderInput={(params) => (
      <TextField {...params} label="Department" variant="outlined" fullWidth />
    )}
  />
</Box>

    </DialogContentText>
  </DialogContent>
  <DialogActions>
    <MDButton onClick={handleEditClose} color="primary">
      Cancel
    </MDButton>
    <MDButton onClick={handleEditSubmit} color="primary" autoFocus>
      Confirm
    </MDButton>
  </DialogActions>
</Dialog>


                <MDTypography variant="h6" color="white" >
                  Add Employee
                </MDTypography>
                <MDButton color="success" onClick={handleOpen}>
                Add Employee
                </MDButton>
              </MDBox>
              <MDBox pt={3}>
                <MDBox pt={3}>
                  <DataTable
                    table={{ columns, rows }}
                    isSorted={false}
